@echo off
REM Sync scripts launcher for Windows
if "%1"=="sync-pull" (
    call C:\Users\cg14849\bin\sync-pull.bat
    goto :eof
)
if "%1"=="sync-push" (
    call C:\Users\cg14849\bin\sync-push.bat
    goto :eof
)
if "%1"=="sync-pull-full" (
    call C:\Users\cg14849\bin\sync-pull-full.bat
    goto :eof
)
if "%1"=="sync-push-full" (
    call C:\Users\cg14849\bin\sync-push-full.bat
    goto :eof
)
if "%1"=="sync-full" (
    call C:\Users\cg14849\bin\sync-full.bat
    goto :eof
)
if "%1"=="sync-status" (
    call C:\Users\cg14849\bin\sync-status.bat
    goto :eof
)
echo Usage: %0 [sync-pull|sync-push|sync-pull-full|sync-push-full|sync-full|sync-status]
echo.
echo Essential sync:
echo   sync-pull          - Pull context and basic settings
echo   sync-push          - Push context and basic settings
echo.
echo Extended sync:
echo   sync-pull-full     - Pull ALL Claude data (sessions, MCP, etc.)
echo   sync-push-full     - Push ALL Claude data
echo   sync-full          - Complete bidirectional sync
echo   sync-status        - Show detailed sync status
