import platform
import socket
import uuid
from pathlib import Path


class PlatformDetector:
    def __init__(self):
        self.system = platform.system()
        self.hostname = socket.gethostname()
        self.machine_id = self._generate_machine_id()

    def is_windows(self):
        return self.system == "Windows"

    def is_macos(self):
        return self.system == "Darwin"

    def is_linux(self):
        return self.system == "Linux"

    def get_system_info(self):
        return {
            "system": self.system,
            "hostname": self.hostname,
            "machine_id": self.machine_id,
            "python_version": platform.python_version(),
            "architecture": platform.architecture()[0]
        }

    def get_machine_identifier(self):
        system_name = "Windows" if self.is_windows() else "macOS" if self.is_macos() else "Linux"
        return f"{self.hostname}-{system_name}"

    def _generate_machine_id(self):
        mac_address = uuid.UUID(int=uuid.getnode()).hex[-12:]
        hostname_hash = str(hash(self.hostname))[-6:]
        return f"{mac_address[:8]}-{hostname_hash}"


def get_platform_detector():
    return PlatformDetector()

