import os
from datetime import datetime, timezone
from pathlib import Path
from .platform_detector import PlatformDetector


class SyncLogger:
    def __init__(self, repo_path=None):
        self.platform = PlatformDetector()
        self.machine_id = self.platform.get_machine_identifier()
        self.repo_path = Path(repo_path) if repo_path else Path.cwd()
        self.log_file = self.repo_path / "SYNC_LOG.md"

    def _get_timestamp(self):
        return datetime.now(timezone.utc).strftime("%Y-%m-%d %H:%M:%S UTC")

    def _format_log_entry(self, operation, files, status, notes=""):
        timestamp = self._get_timestamp()
        files_str = ", ".join(files) if isinstance(files, list) else str(files)

        entry = f"### {timestamp}\n"
        entry += f"- Operation: {operation}\n"
        entry += f"- Files: {files_str}\n"
        entry += f"- Status: {status}\n"
        if notes:
            entry += f"- Notes: {notes}\n"
        entry += "\n"

        return entry

    def log_operation(self, operation, files, status, notes=""):
        if not self.log_file.exists():
            self._initialize_log_file()

        entry = self._format_log_entry(operation, files, status, notes)

        try:
            with open(self.log_file, 'r', encoding='utf-8') as f:
                content = f.read()

            machine_section = f"## {self.machine_id}"

            if machine_section in content:
                lines = content.split('\n')
                new_lines = []
                in_machine_section = False
                section_inserted = False

                for line in lines:
                    if line.startswith("## ") and line != machine_section:
                        if in_machine_section and not section_inserted:
                            new_lines.append(entry.rstrip())
                            new_lines.append("")
                            section_inserted = True
                        in_machine_section = False
                    elif line == machine_section:
                        in_machine_section = True

                    new_lines.append(line)

                if in_machine_section and not section_inserted:
                    new_lines.append(entry.rstrip())
                    new_lines.append("")

                content = '\n'.join(new_lines)
            else:
                content += f"\n## {self.machine_id}\n\n{entry}"

            with open(self.log_file, 'w', encoding='utf-8') as f:
                f.write(content)

        except Exception as e:
            print(f"Warning: Could not write to log file: {e}")

    def _initialize_log_file(self):
        initial_content = """# Sync-Claude Operation Log

## Log Format
Each entry includes:
- Timestamp (UTC)
- Machine identifier
- Operation type (PUSH/PULL/STATUS/INIT/ERROR)
- Files affected
- Status (SUCCESS/ERROR/CONFLICT)
- Notes

---

"""

        with open(self.log_file, 'w', encoding='utf-8') as f:
            f.write(initial_content)

    def get_last_sync_info(self):
        if not self.log_file.exists():
            return None

        try:
            with open(self.log_file, 'r', encoding='utf-8') as f:
                content = f.read()

            machine_section = f"## {self.machine_id}"
            if machine_section not in content:
                return None

            lines = content.split('\n')
            in_machine_section = False
            last_entry = {}

            for line in lines:
                if line.startswith("## ") and line != machine_section:
                    in_machine_section = False
                elif line == machine_section:
                    in_machine_section = True
                elif in_machine_section and line.startswith("### "):
                    timestamp = line.replace("### ", "")
                    last_entry = {"timestamp": timestamp}
                elif in_machine_section and line.startswith("- Operation: "):
                    last_entry["operation"] = line.replace("- Operation: ", "")
                elif in_machine_section and line.startswith("- Status: "):
                    last_entry["status"] = line.replace("- Status: ", "")

            return last_entry if last_entry else None

        except Exception:
            return None

    def get_sync_history(self, limit=10):
        if not self.log_file.exists():
            return []

        try:
            with open(self.log_file, 'r', encoding='utf-8') as f:
                content = f.read()

            entries = []
            lines = content.split('\n')
            current_entry = {}

            for line in lines:
                if line.startswith("### "):
                    if current_entry:
                        entries.append(current_entry)
                    current_entry = {"timestamp": line.replace("### ", "")}
                elif line.startswith("- Operation: "):
                    current_entry["operation"] = line.replace("- Operation: ", "")
                elif line.startswith("- Status: "):
                    current_entry["status"] = line.replace("- Status: ", "")
                elif line.startswith("- Files: "):
                    current_entry["files"] = line.replace("- Files: ", "")
                elif line.startswith("- Notes: "):
                    current_entry["notes"] = line.replace("- Notes: ", "")

            if current_entry:
                entries.append(current_entry)

            return entries[-limit:] if len(entries) > limit else entries

        except Exception:
            return []


def get_sync_logger(repo_path=None):
    return SyncLogger(repo_path)

