import os
import subprocess
from pathlib import Path
from .logger import get_sync_logger


class GitHandler:
    def __init__(self, repo_path=None):
        self.repo_path = Path(repo_path) if repo_path else Path.cwd()
        self.logger = get_sync_logger(self.repo_path)

    def _run_git_command(self, command, capture_output=True):
        try:
            result = subprocess.run(
                command,
                cwd=self.repo_path,
                capture_output=capture_output,
                text=True,
                check=True
            )
            return result.stdout.strip() if capture_output else ""
        except subprocess.CalledProcessError as e:
            error_msg = f"Git command failed: {' '.join(command)}\nError: {e.stderr}"
            raise Exception(error_msg)
        except FileNotFoundError:
            raise Exception("Git is not installed or not in PATH")

    def is_git_repo(self):
        try:
            self._run_git_command(["git", "rev-parse", "--git-dir"])
            return True
        except Exception:
            return False

    def init_repo(self):
        if not self.is_git_repo():
