import json
import hashlib
import re
from pathlib import Path
from typing import Dict, List, Tuple, Any


class MergeEngine:
    def __init__(self):
        self.conflict_markers = {
            'start': '<<<<<<< LOCAL',
            'middle': '=======',
            'end': '>>>>>>> REMOTE'
        }

    def merge_claude_md(self, local_content: str, remote_content: str) -> Tuple[str, List[str]]:
        local_sections = self._parse_markdown_sections(local_content)
        remote_sections = self._parse_markdown_sections(remote_content)

        merged_sections = {}
        conflicts = []

        all_headers = set(local_sections.keys()) | set(remote_sections.keys())

        for header in all_headers:
            local_section = local_sections.get(header, "")
            remote_section = remote_sections.get(header, "")

            if header not in local_sections:
                merged_sections[header] = remote_section
            elif header not in remote_sections:
                merged_sections[header] = local_section
            elif self._content_hash(local_section) == self._content_hash(remote_section):
                merged_sections[header] = local_section
            else:
                conflicts.append(header)
                merged_sections[header] = self._create_conflict_section(
                    header, local_section, remote_section
                )

        merged_content = self._reconstruct_markdown(merged_sections)
        return merged_content, conflicts

    def merge_settings_json(self, local_content: str, remote_content: str) -> Tuple[str, List[str]]:
        try:
            local_data = json.loads(local_content) if local_content.strip() else {}
            remote_data = json.loads(remote_content) if remote_content.strip() else {}
        except json.JSONDecodeError as e:
            return local_content, [f"JSON parsing error: {e}"]

        merged_data, conflicts = self._deep_merge_dict(local_data, remote_data)

        try:
            merged_content = json.dumps(merged_data, indent=2, sort_keys=True)
            return merged_content, conflicts
        except Exception as e:
            return local_content, [f"JSON serialization error: {e}"]

    def _parse_markdown_sections(self, content: str) -> Dict[str, str]:
        sections = {}
        lines = content.split('\n')
        current_header = None
        current_content = []

        for line in lines:
            if line.startswith('#'):
                if current_header is not None:
                    sections[current_header] = '\n'.join(current_content).strip()

                current_header = line.strip()
                current_content = []
            else:
                current_content.append(line)

        if current_header is not None:
            sections[current_header] = '\n'.join(current_content).strip()

        if not sections and content.strip():
            sections['# Content'] = content.strip()

        return sections

    def _reconstruct_markdown(self, sections: Dict[str, str]) -> str:
        content_parts = []

        for header in sorted(sections.keys()):
            content_parts.append(header)
            if sections[header]:
                content_parts.append(sections[header])
            content_parts.append("")

        return '\n'.join(content_parts).rstrip() + '\n'

    def _content_hash(self, content: str) -> str:
        normalized_content = re.sub(r'\s+', ' ', content.strip())
        return hashlib.md5(normalized_content.encode()).hexdigest()

    def _create_conflict_section(self, header: str, local_content: str, remote_content: str) -> str:
        conflict_content = f"{self.conflict_markers['start']}\n"
        conflict_content += f"{local_content}\n"
        conflict_content += f"{self.conflict_markers['middle']}\n"
        conflict_content += f"{remote_content}\n"
        conflict_content += f"{self.conflict_markers['end']}"
        return conflict_content

    def _deep_merge_dict(self, local: Dict[str, Any], remote: Dict[str, Any], path: str = "") -> Tuple[Dict[str, Any], List[str]]:
        merged = local.copy()
        conflicts = []

        for key, remote_value in remote.items():
            current_path = f"{path}.{key}" if path else key

            if key not in local:
                merged[key] = remote_value
            elif isinstance(local[key], dict) and isinstance(remote_value, dict):
                merged[key], sub_conflicts = self._deep_merge_dict(
                    local[key], remote_value, current_path
                )
                conflicts.extend(sub_conflicts)
            elif local[key] == remote_value:
                merged[key] = local[key]
            else:
                conflicts.append(current_path)
                merged[key] = {
                    "_conflict": True,
                    "_local": local[key],
                    "_remote": remote_value,
                    "_resolved": local[key]
                }

        return merged, conflicts

    def has_conflicts(self, content: str) -> bool:
        return any(marker in content for marker in self.conflict_markers.values())

    def extract_conflicts(self, content: str) -> List[Dict[str, str]]:
        conflicts = []
        lines = content.split('\n')
        i = 0

        while i < len(lines):
            if self.conflict_markers['start'] in lines[i]:
                local_lines = []
                remote_lines = []

                i += 1
                while i < len(lines) and self.conflict_markers['middle'] not in lines[i]:
                    local_lines.append(lines[i])
                    i += 1

                if i < len(lines) and self.conflict_markers['middle'] in lines[i]:
                    i += 1
                    while i < len(lines) and self.conflict_markers['end'] not in lines[i]:
                        remote_lines.append(lines[i])
                        i += 1

                conflicts.append({
                    'local': '\n'.join(local_lines),
                    'remote': '\n'.join(remote_lines)
                })

            i += 1

        return conflicts

    def resolve_conflict(self, content: str, resolution: str, conflict_index: int = 0) -> str:
        conflicts = self.extract_conflicts(content)
        if conflict_index >= len(conflicts):
            return content

        lines = content.split('\n')
        conflict_count = 0
        i = 0

        while i < len(lines):
            if self.conflict_markers['start'] in lines[i]:
                if conflict_count == conflict_index:
                    start_line = i

                    i += 1
                    while i < len(lines) and self.conflict_markers['middle'] not in lines[i]:
                        i += 1

                    if i < len(lines):
                        i += 1
                        while i < len(lines) and self.conflict_markers['end'] not in lines[i]:
                            i += 1

                    end_line = i

                    if resolution == 'local':
                        replacement = conflicts[conflict_index]['local'].split('\n')
                    elif resolution == 'remote':
                        replacement = conflicts[conflict_index]['remote'].split('\n')
                    else:
                        replacement = resolution.split('\n')

                    lines[start_line:end_line+1] = replacement
                    break

                conflict_count += 1
            i += 1

        return '\n'.join(lines)


def get_merge_engine():
    return MergeEngine()

