#!/usr/bin/env python3

import os
import sys
import subprocess
import platform
from pathlib import Path


def check_python_version():
    if sys.version_info < (3, 7):
        print("Error: Python 3.7 or higher is required")
        return False
    print(f"Python version: {sys.version}")
    return True


def check_git_installed():
    try:
        result = subprocess.run(["git", "--version"], capture_output=True, text=True)
        print(f"Git version: {result.stdout.strip()}")
        return True
    except FileNotFoundError:
        print("Error: Git is not installed or not in PATH")
        return False


def install_dependencies():
    print("Installing Python dependencies...")
    try:
        subprocess.run([sys.executable, "-m", "pip", "install", "-r", "requirements.txt"], check=True)
        print("Dependencies installed successfully")
        return True
    except subprocess.CalledProcessError as e:
        print(f"Error installing dependencies: {e}")
        return False


def setup_launchers():
    system = platform.system()

    if system == "Windows":
        launcher_script = Path("launchers/sync-claude.bat")
        if launcher_script.exists():
            print("Windows launcher available at: launchers/sync-claude.bat")
            print("You can create a shortcut or add the launchers directory to your PATH")
    else:
        launcher_script = Path("launchers/sync-claude.sh")
