import os
import time
from pathlib import Path
from .platform_detector import PlatformDetector


class PathResolver:
    def __init__(self):
        self.platform = PlatformDetector()
        self._claude_base_path = None

    def get_claude_base_path(self):
        if self._claude_base_path:
            return self._claude_base_path

        if self.platform.is_windows():
            username = os.getenv('USERNAME')
            self._claude_base_path = Path(f"C:/Users/{username}/.claude")
        else:
            self._claude_base_path = Path.home() / ".claude"

        return self._claude_base_path

    def get_claude_memory_path(self):
        return self.get_claude_base_path() / "CLAUDE.md"

    def get_claude_settings_path(self):
        return self.get_claude_base_path() / "settings.json"

    def get_claude_local_settings_path(self):
        return self.get_claude_base_path() / "settings.local.json"

    def get_all_sync_paths(self):
        base_path = self.get_claude_base_path()
        paths = {
            "memory": self.get_claude_memory_path(),
            "settings": self.get_claude_settings_path(),
            "local_settings": self.get_claude_local_settings_path()
        }

        existing_paths = {}
        for key, path in paths.items():
            if path.exists():
                existing_paths[key] = path

        return existing_paths

    def ensure_claude_directory(self):
        claude_path = self.get_claude_base_path()
        if not claude_path.exists():
            claude_path.mkdir(parents=True, exist_ok=True)
        return claude_path

    def get_backup_path(self, original_path):
        timestamp = str(int(time.time()))
        backup_name = f"{original_path.stem}.backup.{timestamp}{original_path.suffix}"
        return original_path.parent / backup_name

    def validate_paths(self):
        base_path = self.get_claude_base_path()

        validation_result = {
            "base_exists": base_path.exists(),
            "base_writable": False,
            "files_found": {},
            "errors": []
        }

        if validation_result["base_exists"]:
            try:
                test_file = base_path / ".sync_test"
                test_file.touch()
                test_file.unlink()
                validation_result["base_writable"] = True
            except PermissionError:
                validation_result["errors"].append("No write permission to Claude directory")

        for key, path in {"memory": self.get_claude_memory_path(), 
                         "settings": self.get_claude_settings_path(),
                         "local_settings": self.get_claude_local_settings_path()}.items():
            validation_result["files_found"][key] = {
                "exists": path.exists(),
                "readable": path.exists() and os.access(path, os.R_OK),
                "writable": path.exists() and os.access(path, os.W_OK)
            }

        return validation_result


def get_path_resolver():
    return PathResolver()

