import json
import hashlib
import re
import difflib
from pathlib import Path
from typing import Dict, List, Tuple, Any, Optional


class MergeEngine:
    def __init__(self):
        self.conflict_markers = {
            'start': '<<<<<<< LOCAL',
            'middle': '=======',
            'end': '>>>>>>> REMOTE'
        }
        self.similarity_threshold = 0.8  # 80% similarity to consider content duplicate

    def merge_claude_md(self, local_content: str, remote_content: str) -> Tuple[str, List[str]]:
        local_sections = self._parse_markdown_sections(local_content)
        remote_sections = self._parse_markdown_sections(remote_content)

        # Normalize and deduplicate sections
        local_sections = self._deduplicate_sections(local_sections)
        remote_sections = self._deduplicate_sections(remote_sections)

        merged_sections = {}
        conflicts = []
        processed_remote_headers = set()

        # Process local sections
        for local_header, local_section in local_sections.items():
            best_match = self._find_best_header_match(local_header, remote_sections)

            if best_match is None:
                # Local only section
                merged_sections[local_header] = local_section
            elif best_match in processed_remote_headers:
                # Already processed this remote section
                continue
            else:
                processed_remote_headers.add(best_match)
                remote_section = remote_sections[best_match]

                # Check content similarity
                if self._are_contents_similar(local_section, remote_section):
                    # Similar content, merge intelligently
                    merged_content = self._merge_similar_content(local_section, remote_section)
                    merged_sections[local_header] = merged_content
                elif self._content_hash(local_section) == self._content_hash(remote_section):
                    # Identical content
                    merged_sections[local_header] = local_section
                else:
                    # Different content, create conflict
                    conflicts.append(local_header)
                    merged_sections[local_header] = self._create_conflict_section(
                        local_header, local_section, remote_section
                    )

        # Add remaining remote sections
        for remote_header, remote_section in remote_sections.items():
            if remote_header not in processed_remote_headers:
                # Check if this is similar to any existing section
                existing_match = self._find_similar_section(remote_section, merged_sections)
                if existing_match is None:
                    merged_sections[remote_header] = remote_section
                # If similar section exists, skip (avoid duplicates)

        merged_content = self._reconstruct_markdown(merged_sections)
        return merged_content, conflicts

    def merge_settings_json(self, local_content: str, remote_content: str) -> Tuple[str, List[str]]:
        if not local_content.strip() and not remote_content.strip():
            return "{}", []

        local_data = json.loads(local_content) if local_content.strip() else {}
        remote_data = json.loads(remote_content) if remote_content.strip() else {}

        merged_data, conflicts = self._deep_merge_dict(local_data, remote_data)

        merged_content = json.dumps(merged_data, indent=2, sort_keys=True)
        return merged_content, conflicts

    def _parse_markdown_sections(self, content: str) -> Dict[str, str]:
        sections = {}
        lines = content.split('\n')
        current_header = None
        current_content = []
        preamble_content = []

        for line in lines:
            if re.match(r'^#+\s', line):  # Match any level header (# ## ### etc)
                # Save previous section
                if current_header is not None:
                    sections[current_header] = '\n'.join(current_content).strip()
                elif preamble_content and not current_header:
                    # Content before first header
                    sections['# Preamble'] = '\n'.join(preamble_content).strip()
                    preamble_content = []

                current_header = line.strip()
                current_content = []
            else:
                if current_header is not None:
                    current_content.append(line)
                else:
                    preamble_content.append(line)

        # Save the last section
        if current_header is not None:
            sections[current_header] = '\n'.join(current_content).strip()
        elif preamble_content:
            sections['# Preamble'] = '\n'.join(preamble_content).strip()

        return sections

    def _deduplicate_sections(self, sections: Dict[str, str]) -> Dict[str, str]:
        deduplicated = {}
        processed_content = {}

        for header, content in sections.items():
            normalized_content = self._normalize_content(content)
            content_hash = self._content_hash(normalized_content)

            if content_hash in processed_content:
                # Duplicate content found, merge headers or prefer better one
                existing_header = processed_content[content_hash]['header']
                if self._is_better_header(header, existing_header):
                    # Remove old entry and use new header
                    del deduplicated[existing_header]
                    deduplicated[header] = content
                    processed_content[content_hash] = {'header': header, 'content': content}
                # Otherwise keep the existing one
            else:
                deduplicated[header] = content
                processed_content[content_hash] = {'header': header, 'content': content}

        return deduplicated

    def _normalize_content(self, content: str) -> str:
        # Remove extra whitespace and normalize line endings
        normalized = re.sub(r'\s+', ' ', content.strip())
        # Remove common formatting differences
        normalized = re.sub(r'[*_`]+', '', normalized)  # Remove markdown formatting
        return normalized.lower()

    def _normalize_header(self, header: str) -> str:
        # Normalize header for comparison
        normalized = re.sub(r'^#+\s*', '', header)  # Remove # markers
        normalized = re.sub(r'[^\w\s]', '', normalized)  # Remove punctuation
        normalized = re.sub(r'\s+', ' ', normalized)  # Normalize whitespace
        return normalized.lower().strip()

    def _find_best_header_match(self, local_header: str, remote_sections: Dict[str, str]) -> Optional[str]:
        local_normalized = self._normalize_header(local_header)

        # First try exact normalized match
        for remote_header in remote_sections.keys():
            if self._normalize_header(remote_header) == local_normalized:
                return remote_header

        # Then try fuzzy matching
        best_match = None
        best_ratio = 0.0

        for remote_header in remote_sections.keys():
            ratio = difflib.SequenceMatcher(
                None, 
                local_normalized, 
                self._normalize_header(remote_header)
            ).ratio()

            if ratio > best_ratio and ratio >= 0.7:  # 70% similarity threshold
                best_ratio = ratio
                best_match = remote_header

        return best_match

    def _are_contents_similar(self, content1: str, content2: str) -> bool:
        norm1 = self._normalize_content(content1)
        norm2 = self._normalize_content(content2)

        if not norm1 or not norm2:
            return norm1 == norm2

        ratio = difflib.SequenceMatcher(None, norm1, norm2).ratio()
        return ratio >= self.similarity_threshold

    def _merge_similar_content(self, local_content: str, remote_content: str) -> str:
        # For similar content, prefer the longer/more complete version
        if len(remote_content.strip()) > len(local_content.strip()):
            return remote_content
        return local_content

    def _find_similar_section(self, content: str, existing_sections: Dict[str, str]) -> Optional[str]:
        for header, existing_content in existing_sections.items():
            if self._are_contents_similar(content, existing_content):
                return header
        return None

    def _is_better_header(self, header1: str, header2: str) -> bool:
        # Prefer headers with proper capitalization and formatting
        if header1.isupper() and not header2.isupper():
            return False  # Prefer mixed case over ALL CAPS
        if not header1.isupper() and header2.isupper():
            return True

        # Prefer longer, more descriptive headers
        if len(header1) > len(header2):
            return True

        # Prefer headers with consistent # levels
        h1_level = len(re.match(r'^#+', header1).group()) if re.match(r'^#+', header1) else 0
        h2_level = len(re.match(r'^#+', header2).group()) if re.match(r'^#+', header2) else 0

        if h1_level <= h2_level:  # Prefer higher level (fewer #)
            return True

        return False

    def _reconstruct_markdown(self, sections: Dict[str, str]) -> str:
        content_parts = []

        # Define section order priority
        section_priority = {
            'general': 0,
            'rules': 1,
            'merged': 2,
            'policy': 3,
            'ascii': 4,
            'gemini': 5,
            'examples': 6,
            'verification': 7,
            'important': 8,
            'notes': 9,
            'when': 10,
            'usage': 11
        }

        def get_section_priority(header: str) -> int:
            header_lower = header.lower()
            for keyword, priority in section_priority.items():
                if keyword in header_lower:
                    return priority
            return 999  # Unknown sections go last

        # Sort sections by priority, then alphabetically
        sorted_headers = sorted(
            sections.keys(),
            key=lambda h: (get_section_priority(h), h.lower())
        )

        for header in sorted_headers:
            if header == '# Preamble' and sections[header].strip():
                # Handle preamble specially (no header)
                content_parts.append(sections[header])
                content_parts.append("")
            else:
                content_parts.append(header)
                if sections[header]:
                    content_parts.append(sections[header])
                content_parts.append("")

        # Clean up multiple consecutive empty lines
        result = '\n'.join(content_parts).rstrip()
        result = re.sub(r'\n{3,}', '\n\n', result)  # Max 2 consecutive newlines

        return result + '\n'

    def _content_hash(self, content: str) -> str:
        normalized_content = self._normalize_content(content)
        return hashlib.md5(normalized_content.encode()).hexdigest()

    def _create_conflict_section(self, header: str, local_content: str, remote_content: str) -> str:
        conflict_content = f"{self.conflict_markers['start']}\n"
        conflict_content += f"{local_content}\n"
        conflict_content += f"{self.conflict_markers['middle']}\n"
        conflict_content += f"{remote_content}\n"
        conflict_content += f"{self.conflict_markers['end']}"
        return conflict_content

    def _deep_merge_dict(self, local: Dict[str, Any], remote: Dict[str, Any], path: str = "") -> Tuple[Dict[str, Any], List[str]]:
        merged = local.copy()
        conflicts = []

        for key, remote_value in remote.items():
            current_path = f"{path}.{key}" if path else key

            if key not in local:
                merged[key] = remote_value
            elif isinstance(local[key], dict) and isinstance(remote_value, dict):
                merged[key], sub_conflicts = self._deep_merge_dict(
                    local[key], remote_value, current_path
                )
                conflicts.extend(sub_conflicts)
            elif local[key] == remote_value:
                merged[key] = local[key]
            else:
                conflicts.append(current_path)
                merged[key] = {
                    "_conflict": True,
                    "_local": local[key],
                    "_remote": remote_value,
                    "_resolved": local[key]
                }

        return merged, conflicts

    def has_conflicts(self, content: str) -> bool:
        return any(marker in content for marker in self.conflict_markers.values())

    def extract_conflicts(self, content: str) -> List[Dict[str, str]]:
        conflicts = []
        lines = content.split('\n')
        i = 0

        while i < len(lines):
            if self.conflict_markers['start'] in lines[i]:
                local_lines = []
                remote_lines = []

                i += 1
                while i < len(lines) and self.conflict_markers['middle'] not in lines[i]:
                    local_lines.append(lines[i])
                    i += 1

                if i < len(lines) and self.conflict_markers['middle'] in lines[i]:
                    i += 1
                    while i < len(lines) and self.conflict_markers['end'] not in lines[i]:
                        remote_lines.append(lines[i])
                        i += 1

                conflicts.append({
                    'local': '\n'.join(local_lines),
                    'remote': '\n'.join(remote_lines)
                })

            i += 1

        return conflicts

    def resolve_conflict(self, content: str, resolution: str, conflict_index: int = 0) -> str:
        conflicts = self.extract_conflicts(content)
        if conflict_index >= len(conflicts):
            return content

        lines = content.split('\n')
        conflict_count = 0
        i = 0

        while i < len(lines):
            if self.conflict_markers['start'] in lines[i]:
                if conflict_count == conflict_index:
                    start_line = i

                    i += 1
                    while i < len(lines) and self.conflict_markers['middle'] not in lines[i]:
                        i += 1

                    if i < len(lines):
                        i += 1
                        while i < len(lines) and self.conflict_markers['end'] not in lines[i]:
                            i += 1

                    end_line = i

                    if resolution == 'local':
                        replacement = conflicts[conflict_index]['local'].split('\n')
                    elif resolution == 'remote':
                        replacement = conflicts[conflict_index]['remote'].split('\n')
                    else:
                        replacement = resolution.split('\n')

                    lines[start_line:end_line+1] = replacement
                    break

                conflict_count += 1
            i += 1

        return '\n'.join(lines)


def get_merge_engine():
    return MergeEngine()
