data UE_a;
set UE;
FORMAT CODICE_MERCEOLOGICO_PRIMARIO $94.;
FORMAT CODICE_MERCEOLOGICO_4DIGIT_A $4.;
FORMAT CODICE_MERCEOLOGICO_4DIGIT $4.;
CODICE_MERCEOLOGICO_PRIMARIO=compress(CODICE_MERCEOLOGICO, '.');
CODICE_MERCEOLOGICO_4DIGIT_A=substr(CODICE_MERCEOLOGICO_PRIMARIO,1,4);
CODICE_MERCEOLOGICO_4DIGIT=compress(CODICE_MERCEOLOGICO_4DIGIT_A, ',-; ');
RUN; 

proc sql;
create table UE_b as
select a.*, b.NACE_Rev_2
from UE_a as a left join RACCORDO_WZ2008_NACE as b
on a.CODICE_MERCEOLOGICO_4DIGIT=b.WZ_2008_Kode_4_digit
where a.TIPO_CODICE_MERCEOLOGICO in ('WZ 2008' 'WZ2008' 'wz 2008' 'wz2008');
quit;

data UE_c;
set UE_b;
FORMAT CODICE_NACE $4.;
CODICE_NACE=NACE_Rev_2;
drop NACE_Rev_2;
run;


proc sql;
create table UE_d as
select a.*, b.CODICE_NACE
from UE_a as a left join NACE_MAP as b
on a.CODICE_MERCEOLOGICO_4DIGIT=b.CODICE_NACE
where a.TIPO_CODICE_MERCEOLOGICO in ('NACE' 'nace' 'Nace' '' ' ');
quit;


data UE_0;
set UE_c UE_D;
run;


proc sql;
create table UE_1 as
select a.*, b.MACROSECTOR, b.MICROSECTOR, b.settore_num
from UE_0 as a left join NACE_MAP as b
on a.CODICE_NACE=b.CODICE_NACE;
quit;


proc freq data=UE_1;
table CD_NAZIONE/ missing;
run;



data DE;
FORMAT SIZE SIZE_GROUP $50.;
format CGS_MODIFIER $13.;
set UE_1 (where=(ID_richiesta >= 0 and DATA_BILANCIO ne . and ESTERO_FATTURATO_BILANCIO_1 ne . AND 
PATRIMONIO_NETTO NE . AND
ONERI_FINANZIARI_1 NE . AND
UTILE_PERDITA_NETTO NE . and
EBIT NE . and
EBITDA NE . and
DEBITI_TOTALI NE . and 
TOTALE_ATTIVO NE .));

debiti = DEBITI_BREVE_TERMINE + DEBITI_LUNGO_TERMINE;
diff = round(debiti - debiti_totali,1);

/* if abs(diff) > 1 then delete; */

if CD_NAZIONE in ("DE");

if STATO_ATTIVITA not in ("" " ");

if EVENTI_NEGATIVI_Si_no not in ("" " ");

diff_data=TODAY()-DATA_BILANCIO;

if diff_data<365*1.5;

if STATO_ATTIVITA = "ATTIVA" 	THEN FLAG_ANOMALA = 0;
								ELSE FLAG_ANOMALA = 1;


IF ESTERO_FATTURATO_BILANCIO_1 NE . and year(DATA_BILANCIO) >= 2016;
if DATA_BILANCIO le today();
anz_aziendale_mesi = round(DATA_EVASIONE_REPORT - DATA_REGISTRAZIONE) / 12;

		IF CD_UNITA_MISURA_BILANCIO = "UNITA" 		THEN K = 1;
ELSE	IF CD_UNITA_MISURA_BILANCIO = "MIGLIAIA" 	THEN K = 1000;
ELSE	IF CD_UNITA_MISURA_BILANCIO = "MILIONI" 	THEN K = 1000000;

K_VALORE_FATTURATO 		 = K*ESTERO_FATTURATO_BILANCIO_1;
K_VALORE_FATTURATO_1 	 = K*ESTERO_FATTURATO_BILANCIO_2;
K_VALORE_FATTURATO_2 	 = K*ESTERO_FATTURATO_BILANCIO_3;
K_UTILE_PERDITA_NETTO 	 = K*UTILE_PERDITA_NETTO;
K_UTILE_PERDITA_NETTO_1	 = K*UTILE_PERDITA_NETTO_2;
K_UTILE_PERDITA_NETTO_2	 = K*UTILE_PERDITA_NETTO_3;
K_EBIT					 = K*EBIT;
K_EBIT_1				 = K*EBIT_2;
K_EBIT_2				 = K*EBIT_3;
K_EBITDA				 = K*EBITDA;
K_EBITDA_1				 = K*EBITDA_2;
K_EBITDA_2				 = K*EBITDA_3;
K_TOTALE_ATTIVO			 = K*TOTALE_ATTIVO;
K_TOTALE_ATTIVO_1		 = K*TOTALE_ATTIVO_2;
K_TOTALE_ATTIVO_2		 = K*TOTALE_ATTIVO_3;
