data DE_2;
format ANDAMENTO_SIST PAGAMENTI_SIST SITUAZIONE_FINANZIARIA_SIST SETTORE SETTORE_AGGR $100.;
set DE_1;

mediana = 0.0622;

	    if MACROSECTOR in ("Administrative and support service activities" "Arts, entertainment and recreation" "Education" 
"Human health and social work activities" "Other services activities" "Professional, scientific and technical activities" "Service" "Transport & Logistic") then SETTORE = "Services";
else 	if MACROSECTOR in ("Construction" "Real estate activities") 																						then SETTORE = "Constructions and Real Estate";
else 	if MACROSECTOR in ("Electricity, gas, steam and air conditioning supply" "Water supply; sewerage; waste managment and remediation activities") 		then SETTORE = "Energy & Utilities";
else 	if MACROSECTOR = "Manifacture" 																														then SETTORE = "Manufacturing";
else 	if MACROSECTOR = "Wholesale + Retail" 																												then SETTORE = "Wholesale and retail trade";
else 	if MACROSECTOR = "Agriculture" or MACROSECTOR = "Financial and insurance" 																			then SETTORE = "Other sectors";
																																							else SETTORE = "Other sectors";

		if SETTORE in ("Manufacturing" "Wholesale and retail trade") 																						THEN SETTORE_AGGR=SETTORE;
																																							else SETTORE_AGGR="Other sectors";
