/* Simulazione rating sistema */


data score_redaz_sist ;
set sample_unrated_8;
format C7_INTG $4.;

*if PAGAMENTI_SIST = "XX - Non disponibile" or ANDAMENTO_SISTEMA = "XX - Non disponibile" then delete;

score_logistico_S = 0.2130 -0.3127*score_SF_S -0.1063*SCORE_A_S -0.4979*SCORE_P_S;

LGT_C8_S = 1/(1+exp(-score_logistico_S));

		if LGT_C8_S <  0.0009	then Score_C8_S = 1;
else 	if LGT_C8_S <  0.0014  	then Score_C8_S = 2;
else 	if LGT_C8_S <  0.0022	then Score_C8_S = 3;
else 	if LGT_C8_S <  0.0028 	then Score_C8_S = 4;
else 	if LGT_C8_S <  0.0050	then Score_C8_S = 5;
else 	if LGT_C8_S <  0.0090 	then Score_C8_S = 6;
else 	if LGT_C8_S <  0.05 	then Score_C8_S = 7;
else 	if LGT_C8_S >= 0.05 	then Score_C8_S = 8;
								else Score_C8_S = -999999999999999999;

		if C7_INTG = "1.1" 	then score_C7_S = 1;
else	if C7_INTG = "1.2" 	then score_C7_S = 1.583333333;
else	if C7_INTG = "1.3" 	then score_C7_S = 2.166666667;
else	if C7_INTG = "2.1" 	then score_C7_S = 2.75;
else	if C7_INTG = "2.2" 	then score_C7_S = 3.333333333;
else	if C7_INTG = "3.0" 	then score_C7_S = 3.916666667;
else	if C7_INTG = "4.0" 	then score_C7_S = 4.5;
else	if C7_INTG = "5.0" 	then score_C7_S = 5.083333333;
else	if C7_INTG = "6.0" 	then score_C7_S = 5.666666667;
else	if C7_INTG = "7.0" 	then score_C7_S = 6.25;
else	if C7_INTG = "8.0" 	then score_C7_S = 6.833333333;
else	if C7_INTG = "9.0" 	then score_C7_S = 7.416666667;
else	if C7_INTG = "10.0" 	then score_C7_S = 8;
								else score_C7_S = 99;

score_comb_S = -13.2571+1.0070*score_C7_S+0.4556*Score_C8_S;

comb_S = 1/(1+exp(-score_comb_S));

		if comb_S <= 0.000014 	then rating_num_S = 1;
else 	if comb_S <= 0.000025	then rating_num_S = 2;
else 	if comb_S <= 0.000035 	then rating_num_S = 3;
else 	if comb_S <= 0.00010 	then rating_num_S = 4;
else 	if comb_S <= 0.00020 	then rating_num_S = 5;
else 	if comb_S <= 0.0004 	then rating_num_S = 6;
else 	if comb_S <= 0.00085 	then rating_num_S = 7;

