# Node.js aliases to use .cmd versions (avoid PowerShell script execution issues)
Set-Alias -Name npm -Value npm.cmd
Set-Alias -Name npx -Value npx.cmd

# Claude Code function - allows running claude from anywhere
function claude {
    npx --prefix "C:\Users\cg14849\Projects" claude $args
}

function gemini { npx https://github.com/google-gemini/gemini-cli $args }

$env:PATH += ";$env:USERPROFILE\bin"$env:PATH += ";$env:USERPROFILE\bin"

